<style>
.animated-signature {
  display: inline;
}
.animated-signature path {
  stroke-dasharray: 2400;
  stroke-dashoffset: 2400;
  fill: transparent;
  animation: drawSignature 12s linear 0.5s forwards;
  stroke-width: 2px;
  stroke: black;
}

@keyframes drawSignature {
  0% {
    stroke-dashoffset: 2400;
  }
  15% {
    fill: transparent;
  }
  35%,
  85% {
    stroke-dashoffset: 0;
    fill: black;
  }
  90%,
  100% {
    stroke-dashoffset: 0; /* 保持路径 */
    fill: black; /* 填充颜色 */
  }
}
</style>
