<style>
.animated-signature {
  display: inline;
  margin-bottom: 2px;
}
.animated-signature path {
  stroke-dasharray: 2400;
  stroke-dashoffset: 2400;
  fill: transparent;
  animation: drawSignature 12s linear 0.5s forwards;
  stroke-width: 4px;
  stroke: #4b5563;
}

@keyframes drawSignature {
  0% {
    stroke-dashoffset: 2400;
  }
  15% {
    fill: transparent;
  }
  35%,
  85% {
    stroke-dashoffset: 0;
    fill: #4b5563;
  }
  90%,
  100% {
    stroke-dashoffset: 0; /* 保持路径 */
    fill: #4b5563; /* 填充颜色 */
  }
}
</style>
